const topButton=document.getElementById("top-btn");function displayOnScroll(){null!=topButton&&(document.body.scrollTop>500||document.documentElement.scrollTop>500?topButton.style.display="flex":topButton.style.display="none")}function scrollToTop(){document.body.scrollTop=0,window.scrollTo({top:0,behavior:"smooth"})}window.addEventListener("scroll",()=>displayOnScroll()),topButton.addEventListener("click",()=>scrollToTop());